/****************************************************************************** Assert.h   ******************************************************************************/#pragma once#undef ASSERT#ifdef __DBG__#define ASSERT( condition)      \        condition ? 0 :         \        __assertion_failed(     \        #condition, __FILE__, __LINE__)#ifdef __cplusplusextern "C" {#endifshort __assertion_failed( char *conditionStr,                          char *fileName, int lineNum);#ifdef __cplusplus}#endif#else#define ASSERT( condition)#endif