/****************************************************************************** SLVGError.cp   ******************************************************************************/#include "Error.h"SLVGErrorProcPtr	gErrorProc = nil;OSErr				gLastError = noErr;long				gErrorRefcon = 0;void SetSLVGErrorHandler(SLVGErrorProcPtr errorProc, long refcon){	gErrorProc = errorProc;	gErrorRefcon = refcon;}OSErr GetLastSLVGError(void){	return gLastError;}