/****************************************************************************** SLVGErrorHandler.cp   ******************************************************************************/#include "ErrorHandler.h"#include "Error.h"extern SLVGErrorProcPtr	gErrorProc;extern OSErr				gLastError;extern long				gErrorRefcon;void	PostFatalError(OSErr err){	gLastError = err;	if(gErrorProc && err != noErr)		(*gErrorProc)(err, gErrorRefcon);}